#include<string.h>#include<stdio.h>#include<iostream>#include<stdbool.h>#include<algorithm>using namespace std;const long long N = 1e9 + 7;long long ans = 0;long long qmi(long long a,long long k,long long p){	long long res = 1;	while(k)	{		if(k & 1) res = res * a % p;		a = a * a %p;		k >>= 1;	}	return res;}long long C(long long a, long long b) // ͨ�������������C(a, b){ 	long long res = 1; 	for (int i = 1, j = a; i < b; i ++, j -- ) 	{ 	 	res = res * j % N; 		res = res * qmi(i, N - 2,N) % N; 	} 	return res;}int main(){	int a,b,n;	n=5;	for (int i = 0; i <= n; i++)	cout <<C(max(i,n-i),n)<<endl;return 0;	cin >> a >> b >> n;ans  = 0;	for (int i = 0; i <= n; i++)	{		int x = i * a;		int y = (n - i) * b;		int tmp = x + y;		int u = 0;		while (tmp)		{			int s = tmp % 10;			if(s != a && s!= b)			{				u = 1;				break;			}			tmp /= 10;		}		if (!u)		{			ans += C(i,n);			ans = ans % N; 		}	}	cout << ans;	return 0;	 } 